import React from "react";
import ReactDOM from "react-dom";
import Table from "./table.js";
import data from "./data/results.json";

import "./styles.css";

export class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { players: [], w: 2, d: 0.5, l: 0, aw: 1, al: 0 };
  }

  componentDidMount() {
    this.setState({players: data.players});
  }

  componentDidUpdate() {
    this.setState(prevState => ({
      players: data.players.map(function(player) {
        return {
         ...player,
          score:
            player.classicalWins * prevState.w +
            player.classicalDraws * prevState.d +
            player.classicalLosses * prevState.l +
            player.armageddonWins * prevState.aw +
            player.armageddonLosses * prevState.al
        };
      })
    }));
  }

  handleChange(event) {
    this.setState({ w: event.target.value });
  }

  render() {
    this.state.players.sort(function(a, b) {
      return b.score - a.score;
    });

    return (
      <div>
        <input type="text" onChange={this.handleChange} />
        <Table players={this.state.players} />
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
