import React from "react";
import ReactDOM from "react-dom";
import Table from "./table.js";
import data from "./data/results.json";

import "./styles.css";

export class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { players: [], w: 2, d: 0.5, l: 0, aw: 1, al: 0 };

    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    this.setState({players: data.players});
  }

  handleChange(event) {
    this.setState({ w: event.target.value });
  }

  render() {
    const p = this.state.players.map(function(player) {
      const w = this.state.w;
      const d = this.state.d;
      const l = this.state.l;
      const aw = this.state.aw;
      const al = this.state.al;

      return {
        ...player,
        score:
        player.classicalWins * w +
        player.classicalDraws * d +
        player.classicalLosses * l +
        player.armageddonWins * aw +
        player.armageddonLosses * al
      };
    }, this);

    return (
      <div>
        <input type="text" onChange={this.handleChange} />
        <Table players={p} />
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
